//Package BinaryLiterals;

public class BinaryLiterals {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=5;
		System.out.println(i);
		int k=101;
		System.out.println(k);
		int j=0B101;
		System.out.println(j);
		int l=0b1000000;
		System.out.println(l);
	}

}
