
public class Pattern3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
			for(int i=1; i<=4; i++) 
			{
			for (int j=1;j<=4;j++)
		      {
				//System.out.printf("* ");
				if(j==2||i==2||j==3||i==3)
				System.out.printf("* ");

				else 
					System.out.printf("  ");//2
				}
				 
			System.out.printf(" \n");
			}
			}

		}


	


